package com.seydanur.fotografpaylasma.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.navigation.Navigationimport androidx.recyclerview.widget.RecyclerViewimport com.seydanur.fotografpaylasma.databinding.RecyclerRowBindingimport com.seydanur.fotografpaylasma.model.Postsimport com.seydanur.fotografpaylasma.view.FeedFragmentDirectionsimport com.squareup.picasso.Picassoclass PostAdapter(private val postList :ArrayList<Posts>): RecyclerView.Adapter<PostAdapter.PostHolder>() {    class PostHolder(val binding:RecyclerRowBinding): RecyclerView.ViewHolder(binding.root){    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): PostHolder {        val recyclerRowBinding=RecyclerRowBinding.inflate(LayoutInflater.from(parent.context),parent,false)        return PostHolder(recyclerRowBinding)    }    override fun getItemCount(): Int {        return postList.size    }    override fun onBindViewHolder(holder: PostHolder, position: Int) {        holder.binding.recyclerEmailText.text=postList[position].email        holder.binding.recyclerCommentText.text=postList[position].comment        Picasso.get().load(postList[position].downloadURL).into(holder.binding.recyclerImageView)        }    }